export default {
  name: 'monthSelector',
  data () {
    return {
      date: null,
      menu: false,
      modal: false
    }
  },
  mounted () {
    this.date = `${this.year}-${this.month}`
  },
  props: {
    month: {
      type: [Number, String],
      default: new Date().getMonth() + 1
    },
    year: {
      type: [Number, String],
      default: new Date().getFullYear()
    }
  },
  methods: {
    updateDate () {
      this.$refs.menu.save(this.date)
      this.$emit('date-changed', {
        date: this.date
      })
    }
  }
}
